import { ADD_TO_CART, REMOVE_FROM_CART } from "./type";

export const addToCart = (items, item) =>(dispatch) =>{
  const cartItems = items.splice();
        let productAlreadyInCart = false;
        cartItems.forEach(item => {
          if (item.id === product.id) {
            productAlreadyInCart = true;
            item.count++;
          }
        });
        if (!productAlreadyInCart) {
          cartItems.push({ ...product, count: 1 });
        }
        localStorage.setItem("cartItems", JSON.stringify(cartItems));
        return dispatch({type : ADD_TO_CART ,
        payload :{
            cartItems : cartItems
        }})

}
export const removeFromCart = (items , product) => (dispatch) =>{
    const cartItems = items.splice().filter(elem => elem.id != item.id);
    localStorage.removeItem("cartItem", cartItems);
    return dispatch({action : REMOVE_FROM_CART, payload:{
        cartItems
    }})
}
