import { FETCH_PRODUCTS } from "../actions/type";

const initialState = { items: [] };
export default function(state = initialState, action){
    switch(action.type){
        case FETCH_PRODUCTS:
        return{items : action.payload};
        default : 
        return state;
    }
}
